import React from "react";

import {
  Container,
  InnerContainer,
  ImageContainer,
  TextContainer,
  Title,
  Description,
  Close,
} from "./styles";
import { getTranslation } from "../../helpers";

interface Props {
  language: string;
  open: boolean;
  setOpen: (open: boolean) => void;
}

export const ModalComponent: React.FunctionComponent<Props> = ({
  language,
  open,
  setOpen,
}) => {
  const closeModal = (): void => setOpen(false);
  return (
    <Container
      id="modalllll"
      style={{ opacity: open ? 1 : 0, visibility: open ? "visible" : "hidden" }}
    >
      <InnerContainer
        style={{
          bottom: open ? 0 : "-100vw",
          right: open ? 0 : "-100vh",
          transform: open ? "rotate(0)" : "rotate(32deg)",
        }}
      >
        <Close onClick={closeModal}>X</Close>
        <ImageContainer>
          <img
            src="https://i.pinimg.com/originals/bf/f0/a7/bff0a7aace3ed9f4893beffbbf6b1e9b.jpg"
            alt=""
          />
        </ImageContainer>
        <TextContainer>
          <Title>Puta que pariu</Title>
          <Description>
            {getTranslation("practice_areas_5_text", language)}
          </Description>
        </TextContainer>
      </InnerContainer>
    </Container>
  );
};
